import { Moralis } from "@moralis/MoralisSvelte";
import { contractInfo } from "@storage/contract";

export async function getDeposits(){
    let account = (await contractInfo()).account
    let allDeposits = await Moralis.Cloud.run("deposits",{ })
    return allDeposits;
}

export async function userGames(){
    let account = (await contractInfo()).account
    let games = await Moralis.Cloud.run("userGames",{ user: account })
    return games;
}

export async function userDeposits(){
    let account = (await contractInfo()).account
    
    let allDeposits = await Moralis.Cloud.run("userDeposits",{ user: account })
    return allDeposits;
}


export async function totalDeposited(){
    let account = (await contractInfo()).account
    let total = await Moralis.Cloud.run("totalDeposited",{ user: account })
    return total
}



