import { Moralis } from "@moralis/MoralisSvelte";
/* HELPER FUNTIONS  */

export function shortAddr(address) {
    var firstPart = address.substr(0, 6);
    var secondPart = address.substr(38, 4);
    var userAddr = firstPart + "...." + secondPart

    return userAddr;
}

//ether to wei
export const getWei = async (value) => {    
    let res = (await Moralis.Web3.enable()).utils.toWei(String(value))
    return res
}

//Wei to ether
export const getEth = async (value) => {    
    let res = (await Moralis.Web3.enable()).utils.fromWei(String(value))
    return res
}
