import { Moralis } from "@moralis/MoralisSvelte";
/* HELPER FUNTIONS  */

export function shortAddr(address){
    var firstPart = address.substr(0, 6);
    var secondPart = address.substr(38, 4);
    var userAddr = firstPart + "...." + secondPart

    return userAddr;
}

//ether to wei
export async function getWei(web3,amount) {
    amount = amount.toString()
    return web3.utils.toWei(amount, "Ether")
}

//Wei to ether
export async function getEth(web3,amount) {
    web3 = await Moralis.Web3.enable();
    amount = amount.toString()
    return web3.utils.fromWei(amount, "Ether")
}
