export function metamaskMessage(message) {

    var metamaskSignature = "MetaMask Tx Signature:"
    if (message.indexOf(metamaskSignature) == 0) {

       message = message.replace(metamaskSignature, "<b>MetaMask Tx Signature:</b>")       

    }
    
    return message

}

export function alertParser(message, alertType) {

    var alertData = {
        show: true,
        alert: alertType,
        content: metamaskMessage(message)
    }

    return alertData

}

