import { writable } from 'svelte/store';
import { alertParser } from "@helpers/messageParser";

export const alertTrigger = writable({ alert:'success',content:'hellow Moralis!',show:false });

export function setAlert(message,alertType){

    alertTrigger.update(value => {
        value = alertParser(message, alertType)
        return value;
    });   
    
}